let acordionItem = document.getElementsByClassName('acordion-box__item');
let acordionH = document.getElementsByClassName('acordion-box__label');

for (i = 0; i < acordionH.length; i++) {
  acordionH[i].addEventListener('click', toggleItem, false);
}

function toggleItem() {
  let itemClass = this.parentNode.className;
  for (i = 0; i < acordionItem.length; i++) {
    acordionItem[i].className = 'acordion-box__item close';
  }
  if (itemClass == 'acordion-box__item close') {
    this.parentNode.className = 'acordion-box__item open';
  }
}

var swiper = new Swiper('.swiper-container', {
  centeredSlides: true,
  navigation: {
    nextEl: '.swiper-button-next',
    prevEl: '.swiper-button-prev',
  },
  breakpoints: {
    // when window width is >= 320px
    320: {
      slidesPerView: 1,
      spaceBetween: 20
    },
    // when window width is >= 480px
    480: {
      slidesPerView: 3,
      spaceBetween: 30
    },
    // when window width is >= 640px
    640: {
      slidesPerView: 4,
      spaceBetween: 40
    }
  }
});